settings.nodeRelationQueryFunction:
// Function should return relations query object for current node used to fetch entity children.
// Function can return 'default' string value. In this case default relations query will be used.

// The following example code will construct simple relations query that will fetch relations of type 'Contains'
// from the current entity.

var entity = nodeCtx.entity;
var query = {
    parameters: {
        rootId: entity.id.id,
        rootType: entity.id.entityType,
        direction: "FROM",
        maxLevel: 1
    },
    filters: [{
        relationType: "Contains",
        entityTypes: []
    }]
};
return query;

settings.nodeHasChildrenFunction:
// Function should return boolean value indicating whether current node has children (whether it can be expanded).

// The following example code will restrict entities hierarchy expansion up to third level.

return nodeCtx.level <= 3;

// The next example code will restrict entities expansion according to the value of example 'nodeHasChildren' attribute.

var data = nodeCtx.data;
if (data.hasOwnProperty('nodeHasChildren') && data['nodeHasChildren'] !== null) {
    return data['nodeHasChildren'] === 'true';
} else {
    return true;
}

settings.nodeTextFunction:
// Function should return text (can be HTML code) for the current node.

// The following example code will generate node text consisting of entity name and temperature if temperature value is present in entity attributes/timeseries.

var data =  nodeCtx.data;
var entity = nodeCtx.entity;
var text = entity.name;
if (data.hasOwnProperty('totalAcikPencere') && data['totalAcikPencere'] !== null && data.hasOwnProperty('totalKapaliPencere') && data['totalKapaliPencere'] !== null) {
    text += " <b><font color=\"#007500\">&nbsp;"+ data['totalAcikPencere'] +"</font></b>&nbsp" + " <b><font color=\"#A60027\">&nbsp;"+ data['totalKapaliPencere'] +"</font></b>";
}
return text;

settings.nodeDisabledFunction:
/**

// Function should return boolean value indicating whether current node should be disabled (not selectable).

// The following example code will disable current node according to the value of example 'nodeDisabled' attribute.

var data = nodeCtx.data;
if (data.hasOwnProperty('nodeDisabled') && data['nodeDisabled'] !== null) {
    return data['nodeDisabled'] === 'true';
} else {
    return false;
}

**/

settings.nodesSortFunction:
/**

// This function is used to sort nodes of the same level. Function should compare two nodes and return 
// integer value: 
//     - less than 0 - sort nodeCtx1 to an index lower than nodeCtx2
//     - 0 - leave nodeCtx1 and nodeCtx2 unchanged with respect to each other
//     - greater than 0 - sort nodeCtx2 to an index lower than nodeCtx1

// The following example code will sort entities first by entity type in alphabetical order then
// by entity name in alphabetical order.

var result = nodeCtx1.entity.id.entityType.localeCompare(nodeCtx2.entity.id.entityType);
if (result === 0) {
    result = nodeCtx1.entity.name.localeCompare(nodeCtx2.entity.name);
}
return result;

**/

settings.nodeOpenedFunction:
// Function should return boolean value indicating whether current node should be opened (expanded) when it first loaded.

// The following example code will open by default nodes up to third level.

return nodeCtx.level <= 1;

settings.nodeIconFunction:
/**

// Function should return node icon info object.
// Resulting object should contain either 'materialIcon' or 'iconUrl' property. 
// Where:
//    - 'materialIcon' - name of the material icon to be used from the Material Icons Library (https://material.io/tools/icons);
//    - 'iconUrl' - url of the external image to be used as node icon.
// Function can return 'default' string value. In this case default icons according to entity type will be used.

// The following example code shows how to use external image for devices which name starts with 'Test' and use 
// default icons for the rest of entities.

var entity = nodeCtx.entity;
if (entity.id.entityType === 'DEVICE' && entity.name.startsWith('Test')) {
    return {iconUrl: 'https://avatars1.githubusercontent.com/u/14793288?v=4&s=117'};
} else {
    return '';
}

**/

